import React from "react";
import { useState } from "react";
import "./GenralStyles.css";

function TripDetailsForms() {
  const [enteredDistance, setEnteredDistance] = useState("");
  const distanceChangeHandler = (event) => {
    setEnteredDistance(event.target.value);
  };

  const [enteredDate, setEnteredDate] = useState("");
  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const [enterdTime, setEnterTime] = useState("");
  const timeChangeHandler = (event) => {
    setEnterTime(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const customerData = {
      spare: enteredDate,
      date: new Date(enteredDate),
    };
    console.log(customerData);
    setEnteredDistance("");
    setEnteredDate("");
  };
  return (
    <div className="container">
      <div className="new-expense">
        <div className="login-header">
          <header className="titles">Trip Details</header>
          {/* <img src="img/trin.png" alt="trin trin logo" className="trin-img" /> */}
        </div>

        <form onSubmit={submitHandler}>
          <div className="new-expense__controls">
            <div className="new-expense__control">
              <label>Date </label>
              <input
                type="date"
                onChange={dateChangeHandler}
                value={enteredDate}
                required
              ></input>
            </div>

            <div className="new-expense__control">
              <label>Distance Travelled</label>
              <input
                type="number"
                placeholder="Enter the distance Travelled"
                onChange={distanceChangeHandler}
                value={enteredDistance}
                required
              ></input>
            </div>

            <div className="new-expense__control">
              <label>Begin Time</label>
              <input
                type="time"
                onChange={timeChangeHandler}
                value={enterdTime}
                required
              ></input>
            </div>

            <div className="new-expense__control">
              <label>End Time</label>
              <input
                type="Time"
                placeholder="Number of Spare Parts Used"
                // onChange={distanceChangeHandler}
                // value={enteredDistance}
                required
              ></input>
            </div>
          </div>
          <div className="new-expense__actions">
            <button type="submit">Next</button>
          </div>
        </form>
      </div>
    </div>
  );
}
export default TripDetailsForms;
